import React from "react";

const AddPlayerForm = (props) => {
    return (
        <form>
            <input
                type = "text"
                value = {props.value}
                placeholder = "Enter a player's name"
            />
            <input 
                type="submit"
                value="Add Player"
                onClick={props.addPlayer}
            />
        </form>
    )
}

export default AddPlayerForm;
